    getName: function getName(item) {
      var address;

      if (this.add === item.to) {
        address = item.from;
      } else {
        address = item.to;
      }

      var user = this.transactName[address]; //  console.log("user", user.companyName)

      if (!user) {
        return address;
      } else if (user.firstName || user.lastName) {
        return user.firstName + ' ' + user.lastName;
      } else if (user.companyName) {
        return user.companyName;
      }
    },
    customSort: function customSort(value) {
      var _this = this;

      return value.sort(function (a, b) {
        var sortBy = _this.currentSort;

        if (_this.currentSortOrder === "desc") {
          return a[sortBy].localeCompare(b[sortBy]);
        }

        return b[sortBy].localeCompare(a[sortBy]);
      });
    },
    handleLike: function handleLike(item) {
