      });
    },
    handleEdit: function handleEdit(item) {
      console.log(item);
      var finalhash = item.hash;
      popupCenter("http://explorer.evenscoin.io/tx/" + finalhash, 'myPop1', 850, 500);

      function popupCenter(url, title, w, h) {
        var left = screen.width / 2 - w / 2;
        var top = screen.height / 2 - h / 2;
        return window.open(url, title, 'toolbar=no, location=no, directories=no, status=no, menubar=no, scrollbars=no, resizable=no, copyhistory=no, width=' + w + ', height=' + h + ', top=' + top + ', left=' + left);
      }
    },
    handleDelete: function handleDelete(item) {
      var _this2 = this;

      swal({
        title: "Are you sure?",
        text: "You won't be able to revert this!",
        type: "warning",
        showCancelButton: true,
        confirmButtonClass: "md-button md-success btn-fill",
        cancelButtonClass: "md-button md-danger btn-fill",
        confirmButtonText: "Yes, delete it!",
        buttonsStyling: false
      }).then(function (result) {
        if (result.value) {
          _this2.deleteRow(item);

          swal({
            title: "Deleted!",
            text: "You deleted ".concat(item.name),
            type: "success",
            confirmButtonClass: "md-button md-success btn-fill",
            buttonsStyling: false
          });
        }
      });
    },
    deleteRow: function deleteRow(item) {
      var indexToDelete = this.tableData.findIndex(function (tableRow) {
        return tableRow.id === item.id;
      });

      if (indexToDelete >= 0) {
        this.tableData.splice(indexToDelete, 1);
      }
    }
  },
  mounted: function mounted() {
    // Fuse search initialization.
    this.fuseSearch = new Fuse(this.tableData, {
      keys: ["name", "email"],
      threshold: 0.3
    });
  },
  watch: {
    /**
     * Searches through the table data by a given query.
     * NOTE: If you have a lot of data, it's recommended to do the search on the Server Side and only display the results here.
     * @param value of the query
     */
    searchQuery: function searchQuery(value) {
      var result = this.tableData;

      if (value !== "") {
        result = this.fuseSearch.search(this.searchQuery);
      }

      this.searchedData = result;
    }
